mapper.select:
select st_asmvt(result, #{visualId}) tile
from(
select st_asmvtgeom(geom, ST_Transform(
    st_makeenvelope(
        #{xMin,jdbcType=NUMERIC}, #{yMin,jdbcType=NUMERIC},
        #{xMax,jdbcType=NUMERIC},#{yMax,jdbcType=NUMERIC}, 4326
        ), 3857
    ),
    4096, 0, true
    ) as geom

    ,${field}

from ${name}
) as result
